import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

function UserItem(props) {
  return (
    <tr>
      <td>
        <img src={props.employee.picture} />
      </td>
      <td>{props.employee.name}</td>
      <td>{props.employee.email}</td>
      <td>{props.employee.phone}</td>
      <td>{props.employee.age}</td>
    </tr>
  );
}
// functional component
function App() {
  const url = "https://randomuser.me/api/?results=200&nat=us";
  // set up the state!
  const [employees, setEmployees] = useState([]);
  const [sorted, setSorted] = useState([])
  // retrieve the data (componentDidMount)
  useEffect(
    () => {
      axios.get(url).then(response => {
        const data = response.data.results;
        const formattedData = data.map(function(item) {
          return {
            name: `${item.name.first} ${item.name.last}`,
            picture: item.picture.thumbnail,
            email: item.email,
            phone: item.phone,
            age: item.dob.age
          };
        });
        setEmployees(formattedData);
        setSorted(formattedData)
      });
    },
    [
      /* dependency list */
    ]
  );

  function handleSort(val) {
    console.log(val)
    const data = employees.sort((a,b)=>
        a[val] < b[val] ? 1 : a[val] > b[val] ? -1 : 0
    )
    console.log(data)
    setSorted(data)
  }
  // return the rendered code
  return (
    <React.Fragment>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <a className="navbar-brand" href="#">
          Employee Directory
        </a>
      </nav>
      <article className="container">
        <table class="table">
          <thead>
            <tr>
              <th scope="col">Picture</th>
              <th onClick={()=>handleSort('name')} scope="col">Name</th>
              <th onClick={()=>handleSort('email')} scope="col">Email</th>
              <th onClick={()=>handleSort('phone')} scope="col">Phone</th>
              <th onClick={()=>handleSort('age')} scope="col">Age</th>
            </tr>
          </thead>
          <tbody>
            {sorted.map(employee => (
              <UserItem employee={employee} />
            ))}
          </tbody>
        </table>
      </article>
    </React.Fragment>
  );
}
export default App;
