import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function UserItem(props) {
    return (
    <tr>
      <td><img src={props.employee.picture}/></td>
      <td>{props.employee.name}</td>
      <td>{props.employee.email}</td>
      <td>{props.employee.phone}</td>
    </tr>
  );
}
// functional component
function App() {
    const url = 'https://randomuser.me/api/?results=200&nat=us';
    // set up the state!
    const [employees, setEmployees] = useState([]);
    // retrieve the data (componentDidMount)
    useEffect(() => {
        axios.get(url).then(response => {
            const data = response.data.results;
            const formattedData = data.map(function (item) {
                return {
                    name: `${item.name.first} ${item.name.last}`,
                    picture: item.picture.thumbnail,
                    email: item.email,
                    phone: item.phone,
                    age: item.age
                }
            });
            setEmployees(formattedData);
        });
    }, [/* dependency list */]);
    // return the rendered code
    return (
        <React.Fragment>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <a className="navbar-brand" href="#">Employee Directory</a>
            </nav>
            <article className="container">
            <table class="table">
            <thead>
            <tr>
                <th scope="col">Picture</th>
            <th scope="col">Name</th>
      <th scope="col">Email</th>
      <th scope="col">Phone</th>
      <th scope="col">Age</th>
    </tr>
  </thead>
  <tbody>
                    {employees.map(employee => <UserItem employee={employee} />)}
                    </tbody>
                    </table>

            </article>
        </React.Fragment>
    );
}
export default App;
