import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function UserItem(props) {
    return (
    <li className="list-group-item">
        <img src={props.employee.picture.thumbnail} />
        <h1>{props.employee.name}</h1>
    </li>);
}
// functional component
function App() {
    const url = 'https://randomuser.me/api/?results=200&nat=us';
    // set up the state!
    const [employees, setEmployees] = useState([]);
    // retrieve the data (componentDidMount)
    useEffect(() => {
        axios.get(url).then(response => {
            const data = response.data.results;
            const formattedData = data.map(function (item) {
                return {
                    name: `${item.name.first} ${item.name.last}`,
                    picture: item.picture
                }
            });
            setEmployees(formattedData);
        });
    }, [/* dependency list */]);
    // return the rendered code
    return (
        <React.Fragment>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <a className="navbar-brand" href="#">Employee Directory</a>
            </nav>
            <article className="container">
                <ul className="list-group">
                    {employees.map(employee => <UserItem employee={employee} />)}
                </ul>
            </article>
        </React.Fragment>
    );
}
export default App;
